//var app = module.exports = require("koa")();

var Koa = require('koa');
var app = new Koa();

app.use(function *(){
	this.body = "Koa says Hi!";
});

var port = process.env.PORT || (process.argv[2] || 3000);
port = (typeof port === "number") ? port : 3000;

app.listen(port);
console.log("Application started. Listening on port:" + port);